# Wait for Decrypt Finish param ( [string] $DriveLetter ) Write-Output "Waiting on Decrypt...." A $encryption = Get-WmiObject -Namespace "root/cimv2/security/microsoftvolumeencryption" -Class "win32_encryptablevolume" -filter "DriveLetter = '$DriveLetter'" $protectionState = $encryption.GetConversionStatus() #The reason for the 100 - is because the percentage reported here goes from 100 to 0 #And we want to report percentage from 0 to 100. $Percent = 100 - $protectionState.EncryptionPercentage Write-Output "Percent Start $Percent"
# If Percent is empty, make sure the loop doesn't run if($Percent) { while($Percent -le '100'){ Start-Sleep -Seconds 10 $encryption = Get-WmiObject -Namespace "root/cimv2/security/microsoftvolumeencryption" -Class "win32_encryptablevolume" -filter "DriveLetter = '$DriveLetter'" $protectionState = $encryption.GetConversionStatus() $Percent = 100 - $protectionState.EncryptionPercentage
Write-Progress -activity "Decryption Progress" -PercentComplete $Percent -status "Decryption Progress" } } else { Write-Output "Error returning Decryption Percentage. Decrypt Script stopped." }
